// Prepare AI prompt from database template - COMPACT CSV FORMAT
const data = $input.first().json;
const projectData = data.projectData;
const keywords = data.keywords;

// Build keyword list in compact CSV format: ID|keyword
const keywordList = keywords.map(kw => `${kw.id}|${kw.keyword}`).join('\n');

// Get system prompt from database and replace template variables
let prompt = projectData.system_prompt || '';

if (!prompt) {
  prompt = `Keyword filtreleme. Ana konu: ${projectData.main_keyword}\nKEYWORDS (ID|keyword):\n${keywordList}\nÇIKTI (sadece CSV, başlık yok):\nID|status|intent|cluster`;
}

// Replace template variables
prompt = prompt
  .replace(/\{\{main_keyword\}\}/g, projectData.main_keyword || '')
  .replace(/\{\{client_name\}\}/g, projectData.client_name || '')
  .replace(/\{\{target_audience\}\}/g, projectData.target_audience || '')
  .replace(/\{\{scenario_type\}\}/g, projectData.scenario_type || '')
  .replace(/\{\{keyword_list\}\}/g, keywordList);

return [{
  json: {
    project_id: projectData.project_id,
    client_id: projectData.client_id,
    main_keyword: projectData.main_keyword,
    enable_ai_analysis: projectData.enable_ai_analysis,
    ai_model: projectData.ai_model,
    ai_temperature: projectData.ai_temperature || 0.7,
    prompt: prompt,
    keyword_count: keywords.length,
    keywords: keywords
  }
}];

