// Mock AI filtreleme - fallback when both APIs fail
const promptData = $('Code - Prepare AI Prompt').first().json;
const keywords = promptData.keywords || [];

const approved = [];
const rejected = [];
const clusterMap = {};

keywords.forEach((kw, idx) => {
  const keyword = kw.keyword.toLowerCase();

  // Eleme kriterleri
  if (keyword.length < 3) {
    rejected.push({ keyword_id: kw.id, keyword: kw.keyword, rejection_reason: 'Cok kisa keyword' });
    return;
  }

  if (kw.search_volume && kw.search_volume < 10) {
    rejected.push({ keyword_id: kw.id, keyword: kw.keyword, rejection_reason: 'Cok dusuk arama hacmi' });
    return;
  }

  // Intent belirleme
  let intent = 'informational';
  if (keyword.includes('fiyat') || keyword.includes('satin al') || keyword.includes('nereden')) {
    intent = 'commercial';
  } else if (keyword.includes('siparis') || keyword.includes('indir')) {
    intent = 'transactional';
  } else if (keyword.includes('nedir') || keyword.includes('nasil')) {
    intent = 'informational';
  }

  // Firsat skoru hesapla
  let score = 50;
  if (kw.search_volume > 1000) score += 20;
  else if (kw.search_volume > 500) score += 10;

  if (kw.keyword_difficulty && kw.keyword_difficulty < 30) score += 15;
  else if (kw.keyword_difficulty && kw.keyword_difficulty < 50) score += 5;

  if (kw.keyword_type === 'long_tail') score += 10;
  if (kw.keyword_type === 'question') score += 5;

  // Kume belirleme
  let cluster = 'Genel';
  if (keyword.includes('nedir') || keyword.includes('ne ise yarar')) {
    cluster = 'Bilgi Icerikleri';
  } else if (keyword.includes('fiyat') || keyword.includes('maliyet')) {
    cluster = 'Fiyat Karsilastirma';
  } else if (keyword.includes('nasil') || keyword.includes('yapilir')) {
    cluster = 'Rehber Icerikleri';
  } else if (keyword.includes('en iyi') || keyword.includes('onerisi')) {
    cluster = 'Karsilastirma Icerikleri';
  }

  if (!clusterMap[cluster]) clusterMap[cluster] = [];
  clusterMap[cluster].push(kw.keyword);

  approved.push({
    keyword_id: kw.id,
    keyword: kw.keyword,
    search_intent: intent,
    opportunity_score: Math.min(score, 100),
    priority: score >= 70 ? 'high' : score >= 50 ? 'medium' : 'low',
    cluster_suggestion: cluster,
    reasoning: `${kw.keyword_type} keyword, ${kw.search_volume || 'bilinmeyen'} hacim`
  });
});

return {
  project_id: promptData.project_id,
  ai_response: {
    summary: `[MOCK - API Fallback] ${keywords.length} keyword analiz edildi. ${approved.length} onaylandi, ${rejected.length} elendi.`,
    total_input: keywords.length,
    total_approved: approved.length,
    total_rejected: rejected.length,
    approved_keywords: approved,
    rejected_keywords: rejected,
    cluster_map: clusterMap
  },
  ai_source: 'mock'
};

// Gemini response parse - CSV FORMAT: ID|status|intent|cluster
const inputData = $input.first().json;
const projectData = $('Code - Prepare AI Prompt').first().json;
const originalKeywords = projectData.keywords || [];

let rawContent = inputData.candidates[0].content.parts[0].text;

// Clean markdown if present
let csvContent = rawContent.replace(/```[a-z]*\n?/g, '').replace(/```/g, '').trim();

// Parse CSV lines
const lines = csvContent.split('\n').filter(l => l.trim() && !l.startsWith('ID|'));
const approved = [];
const rejected = [];
const clusterMap = {};

for (const line of lines) {
  const parts = line.split('|').map(p => p.trim());
  if (parts.length < 4) continue;

  const [id, status, intent, cluster] = parts;
  const kwId = parseInt(id);
  const origKw = originalKeywords.find(k => k.id === kwId);
  if (!origKw) continue;

  if (status === 'approved' || status === 'onay') {
    approved.push({
      keyword_id: kwId,
      keyword: origKw.keyword,
      search_intent: intent,
      opportunity_score: 70,
      priority: 'medium',
      cluster_suggestion: cluster
    });
    if (!clusterMap[cluster]) clusterMap[cluster] = [];
    clusterMap[cluster].push(origKw.keyword);
  } else {
    rejected.push({ keyword_id: kwId, keyword: origKw.keyword, rejection_reason: status });
  }
}

const aiResponse = {
  summary: `${originalKeywords.length} keyword analiz edildi. ${approved.length} onaylandı, ${rejected.length} elendi.`,
  total_input: originalKeywords.length,
  total_approved: approved.length,
  total_rejected: rejected.length,
  approved_keywords: approved,
  rejected_keywords: rejected,
  cluster_map: clusterMap
};

return { project_id: projectData.project_id, ai_response: aiResponse, ai_source: 'gemini' };

