// Calculate token usage from AI responses
const data = $input.first().json;
const projectData = $('Code - Prepare AI Prompt').first().json;
const aiSource = data.ai_source;

let tokensInput = 0;
let tokensOutput = 0;
let modelName = '';
let apiProvider = '';

// Estimate tokens based on character count (rough estimate: chars/4)
const promptLength = projectData.prompt ? projectData.prompt.length : 0;
tokensInput = Math.ceil(promptLength / 4);

if (aiSource === 'gemini') {
  const geminiResponse = $('Gemini - Try First').first().json;
  const responseText = geminiResponse?.candidates?.[0]?.content?.parts?.[0]?.text || '';
  tokensOutput = Math.ceil(responseText.length / 4);
  apiProvider = 'gemini';
  modelName = 'gemini-2.0-flash';
} else if (aiSource === 'openai') {
  const openaiResponse = $('OpenAI - Fallback').first().json;
  const responseText = openaiResponse?.choices?.[0]?.message?.content || '';
  tokensOutput = Math.ceil(responseText.length / 4);
  apiProvider = 'openai';
  modelName = 'gpt-4';
} else if (aiSource === 'mock') {
  // Mock fallback - minimal token usage
  tokensInput = Math.ceil(promptLength / 4);
  tokensOutput = 100;
  apiProvider = 'mock';
  modelName = 'mock-fallback';
}

return {
  project_id: data.project_id,
  client_id: projectData.client_id,
  ai_source: aiSource,
  ai_response: data.ai_response,
  token_tracking: {
    api_provider: apiProvider,
    model_name: modelName,
    tokens_input: tokensInput,
    tokens_output: tokensOutput,
    requests_count: 1,
    was_successful: 1
  }
};

