// Input validation - supports both numeric ID and UUID
const projectId = $json.params?.projectId;

if (!projectId) {
  return {
    json: {
      isValid: false,
      error: 'project_id parametresi zorunludur',
      errorCode: 400
    }
  };
}

// Check if it's a valid UUID format
const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
const isUuid = uuidRegex.test(projectId);

// Check if it's a valid numeric ID
const parsedId = parseInt(projectId);
const isNumericId = !isNaN(parsedId) && parsedId > 0;

if (!isUuid && !isNumericId) {
  return {
    json: {
      isValid: false,
      error: 'project_id gecerli bir sayi veya UUID olmalidir',
      errorCode: 400
    }
  };
}

const query = $json.query || {};
const limit = Math.min(Math.max(parseInt(query.limit) || 100, 1), 500);
const offset = Math.max(parseInt(query.offset) || 0, 0);

return {
  json: {
    isValid: true,
    projectId: isUuid ? projectId : parsedId,
    isUuid: isUuid,
    limit: limit,
    offset: offset
  }
};

// Check if project exists
const result = $input.first().json;
const validatedData = $('Code - Validate Input').first().json;

if (!result.project_id) {
  return [{
    json: {
      projectExists: false,
      projectId: validatedData.projectId
    }
  }];
}

// Use the actual numeric project_id from DB (important when UUID was used)
return [{
  json: {
    projectExists: true,
    projectId: result.project_id,
    projectName: result.project_name,
    mainKeyword: result.main_keyword,
    status: result.status,
    limit: validatedData.limit,
    offset: validatedData.offset
  }
}];

// Aggregate results
const keywordItems = $('MySQL - Get Keywords').all();
const statsResult = $input.first().json;
const projectData = $('IF - Project Exists').first().json;

// Check if keywords exist
const hasKeywords = keywordItems.length > 0 && !!keywordItems[0].json.id;
const keywords = hasKeywords ? keywordItems.map(i => i.json) : [];

// Group by type
const byType = {};
keywords.forEach(kw => {
  const type = kw.keyword_type || 'unknown';
  if (!byType[type]) byType[type] = 0;
  byType[type]++;
});

// Group by cluster
const byCluster = {};
keywords.forEach(kw => {
  if (kw.keyword_cluster) {
    if (!byCluster[kw.keyword_cluster]) byCluster[kw.keyword_cluster] = 0;
    byCluster[kw.keyword_cluster]++;
  }
});

// Group by intent
const byIntent = {};
keywords.forEach(kw => {
  if (kw.search_intent) {
    if (!byIntent[kw.search_intent]) byIntent[kw.search_intent] = 0;
    byIntent[kw.search_intent]++;
  }
});

return [{
  json: {
    success: true,
    project: {
      id: projectData.projectId,
      name: projectData.projectName,
      main_keyword: projectData.mainKeyword,
      status: projectData.status
    },
    data: keywords,
    stats: {
      total: parseInt(statsResult.total_count) || 0,
      filtered_count: keywords.length,
      with_intent: parseInt(statsResult.with_intent) || 0,
      avg_volume: Math.round(parseFloat(statsResult.avg_volume) || 0),
      avg_difficulty: Math.round(parseFloat(statsResult.avg_difficulty) || 0),
      avg_opportunity: Math.round(parseFloat(statsResult.avg_opportunity) || 0)
    },
    by_type: byType,
    by_cluster: byCluster,
    by_intent: byIntent,
    pagination: {
      limit: projectData.limit,
      offset: projectData.offset,
      returned: keywords.length,
      has_more: keywords.length === projectData.limit
    }
  }
}];
