// Input validation
const projectId = $json.params?.projectId;
const format = $json.query?.format || 'json'; // json, csv, html

if (!projectId) {
  return {
    json: {
      isValid: false,
      error: 'projectId parametresi zorunludur',
      errorCode: 400
    }
  };
}

const parsedId = parseInt(projectId);
if (isNaN(parsedId) || parsedId <= 0) {
  return {
    json: {
      isValid: false,
      error: 'projectId gecerli bir pozitif sayi olmalidir',
      errorCode: 400
    }
  };
}

const validFormats = ['json', 'csv', 'html'];
if (!validFormats.includes(format)) {
  return {
    json: {
      isValid: false,
      error: 'Gecersiz format. Gecerli degerler: json, csv, html',
      errorCode: 400
    }
  };
}

return {
  json: {
    isValid: true,
    projectId: parsedId,
    format: format
  }
};

