// Tum verileri topla ve rapor olustur
const validatedData = $('Code - Validate Input').first().json;
const format = validatedData.format;
const project = $('MySQL - Get Project').first().json;

// Keywords - de-duplicate by id (n8n may duplicate rows per input item)
const keywordItems = $('MySQL - Get Keywords').all();
const seenKeywordIds = new Set();
const keywords = keywordItems.map(i => i.json).filter(k => {
  if (!k.id || seenKeywordIds.has(k.id)) return false;
  seenKeywordIds.add(k.id);
  return true;
});

// Competitors - de-duplicate by id (n8n duplicates per input item)
const competitorItems = $('MySQL - Get Competitors').all();
const seenCompetitorIds = new Set();
const competitors = competitorItems.map(i => i.json).filter(c => {
  if (!c.id || seenCompetitorIds.has(c.id)) return false;
  seenCompetitorIds.add(c.id);
  return true;
});

// SERP Features - take first valid result
const serpItems = $('MySQL - Get SERP Features').all();
const serpFeatures = serpItems.find(i => i.json.has_featured_snippet !== undefined)?.json || null;

// PAA - de-duplicate by id
const paaItems = $('MySQL - Get PAA').all();
const seenPaaIds = new Set();
const paaData = paaItems.map(i => i.json).filter(p => {
  if (!p.id || seenPaaIds.has(p.id)) return false;
  seenPaaIds.add(p.id);
  return true;
});

// Filter competitors with valid word count
const competitorsWithWordCount = competitors.filter(c => c.word_count && c.word_count > 0);

// ============ STATISTICS ============
const stats = {
  total_keywords: keywords.length,
  avg_volume: keywords.length > 0 ? Math.round(keywords.reduce((sum, k) => sum + (k.search_volume || 0), 0) / keywords.length) : 0,
  avg_difficulty: keywords.length > 0 ? Math.round(keywords.reduce((sum, k) => sum + (k.keyword_difficulty || 0), 0) / keywords.length) : 0,
  avg_opportunity_score: keywords.length > 0 ? Math.round(keywords.reduce((sum, k) => sum + (parseFloat(k.opportunity_score) || 0), 0) / keywords.length) : 0,
  total_competitors: competitors.length,
  avg_competitor_word_count: competitorsWithWordCount.length > 0 ? Math.round(competitorsWithWordCount.reduce((sum, c) => sum + c.word_count, 0) / competitorsWithWordCount.length) : 0,
  total_paa_questions: paaData.length
};

// Priority breakdown
const priorityBreakdown = {
  high: keywords.filter(k => k.content_priority === 'high').length,
  medium: keywords.filter(k => k.content_priority === 'medium').length,
  low: keywords.filter(k => k.content_priority === 'low').length
};

// Intent distribution
const intentGroups = {};
keywords.forEach(k => {
  const intent = k.search_intent || 'unknown';
  intentGroups[intent] = (intentGroups[intent] || 0) + 1;
});

// Content format distribution
const formatGroups = {};
keywords.forEach(k => {
  const fmt = k.content_format || 'unassigned';
  formatGroups[fmt] = (formatGroups[fmt] || 0) + 1;
});

// Cluster groups
const clusters = {};
keywords.forEach(k => {
  const cluster = k.keyword_cluster || 'Unclustered';
  if (!clusters[cluster]) clusters[cluster] = [];
  clusters[cluster].push(k.keyword);
});

// ============ CONTENT PLAN ============
// Pillar ve Cluster sayfaları
const pillarPages = keywords.filter(k => k.page_type === 'pillar');
const clusterPages = keywords.filter(k => k.page_type === 'cluster');

// Toplam kelime hedefi
const totalWordTarget = keywords.reduce((sum, k) => sum + (k.recommended_word_count || 0), 0);

// Öncelikli içerik listesi
const highPriorityContent = keywords.filter(k => k.content_priority === 'high').sort((a, b) => (parseFloat(b.opportunity_score) || 0) - (parseFloat(a.opportunity_score) || 0));
const mediumPriorityContent = keywords.filter(k => k.content_priority === 'medium').sort((a, b) => (parseFloat(b.opportunity_score) || 0) - (parseFloat(a.opportunity_score) || 0));
const lowPriorityContent = keywords.filter(k => k.content_priority === 'low').sort((a, b) => (parseFloat(b.opportunity_score) || 0) - (parseFloat(a.opportunity_score) || 0));

// Pillar-Cluster haritası
const pillarClusterMap = {};
pillarPages.forEach(pillar => {
  const pillarKeyword = pillar.keyword;
  pillarClusterMap[pillarKeyword] = {
    pillar: pillar,
    clusters: clusterPages.filter(c => c.parent_topic === pillarKeyword || c.keyword_cluster === pillar.keyword_cluster)
  };
});

// Content Plan Summary
const contentPlan = {
  total_content_pieces: keywords.filter(k => k.page_type).length,
  pillar_pages: pillarPages.length,
  cluster_pages: clusterPages.length,
  total_word_target: totalWordTarget,
  estimated_articles: {
    high_priority: highPriorityContent.length,
    medium_priority: mediumPriorityContent.length,
    low_priority: lowPriorityContent.length
  },
  content_by_format: formatGroups,
  pillar_cluster_map: pillarClusterMap
};

// ============ CSV GENERATION ============
// Keywords CSV
const keywordsCsvHeader = 'ID,Keyword,Type,Volume,Difficulty,CPC,Intent,Cluster,Opportunity Score,Page Type,Content Format,Priority,Word Count';
const keywordsCsvRows = keywords.map(k => 
  `${k.id},"${(k.keyword || '').replace(/"/g, '""')}","${k.keyword_type || ''}",${k.search_volume || 0},${k.keyword_difficulty || 0},${k.cpc || 0},"${k.search_intent || ''}","${(k.keyword_cluster || '').replace(/"/g, '""')}",${k.opportunity_score || 0},"${k.page_type || ''}","${k.content_format || ''}","${k.content_priority || ''}",${k.recommended_word_count || 0}`
);
const keywordsCsv = keywordsCsvHeader + '\n' + keywordsCsvRows.join('\n');

// Competitors CSV
const competitorsCsvHeader = 'ID,URL,Domain,Position,Title,Word Count,H1,H2,H3,DR,Backlinks';
const competitorsCsvRows = competitors.map(c => 
  `${c.id},"${c.competitor_url || ''}","${c.competitor_domain || ''}",${c.serp_position || 0},"${(c.title || '').replace(/"/g, '""')}",${c.word_count || 0},${c.h1_count || 0},${c.h2_count || 0},${c.h3_count || 0},${c.domain_rating || 0},${c.backlinks_count || 0}`
);
const competitorsCsv = competitorsCsvHeader + '\n' + competitorsCsvRows.join('\n');

// PAA CSV
const paaCsvHeader = 'ID,Question,Answer,Source URL,Source Domain,Position';
const paaCsvRows = paaData.map(p => 
  `${p.id},"${(p.question || '').replace(/"/g, '""')}","${(p.answer_snippet || '').replace(/"/g, '""')}","${p.source_url || ''}","${p.source_domain || ''}",${p.position || 0}`
);
const paaCsv = paaCsvHeader + '\n' + paaCsvRows.join('\n');

// Summary CSV
const summaryCsv = `Metric,Value
Project Name,"${project.project_name}"
Main Keyword,"${project.main_keyword}"
Client,"${project.client_name || 'N/A'}"
Status,"${project.status}"
Total Keywords,${stats.total_keywords}
Average Volume,${stats.avg_volume}
Average Difficulty,${stats.avg_difficulty}
Average Opportunity Score,${stats.avg_opportunity_score}
High Priority Keywords,${priorityBreakdown.high}
Medium Priority Keywords,${priorityBreakdown.medium}
Low Priority Keywords,${priorityBreakdown.low}
Total Competitors,${stats.total_competitors}
Avg Competitor Word Count,${stats.avg_competitor_word_count}
Total PAA Questions,${stats.total_paa_questions}
Generated At,"${new Date().toISOString()}"`;

// ============ HTML REPORT (for PDF) ============
const topKeywords = keywords.slice(0, 20);
const topKeywordsHtml = topKeywords.map(k => 
  `<tr><td>${k.keyword}</td><td>${k.search_volume || 0}</td><td>${k.keyword_difficulty || 0}</td><td>${k.opportunity_score || 0}</td><td>${k.content_priority || '-'}</td><td>${k.content_format || '-'}</td></tr>`
).join('');

const competitorsHtml = competitors.slice(0, 10).map(c => 
  `<tr><td>${c.competitor_domain || ''}</td><td>${c.serp_position || ''}</td><td>${c.word_count || 0}</td><td>${c.domain_rating || 0}</td><td>${c.backlinks_count || 0}</td></tr>`
).join('');

const paaHtml = paaData.slice(0, 10).map(p => 
  `<tr><td>${p.question || ''}</td><td>${(p.answer_snippet || '').substring(0, 100)}...</td></tr>`
).join('');

// ============ MODERN HTML REPORT ============
// Calculate percentages for charts
const totalWithPriority = priorityBreakdown.high + priorityBreakdown.medium + priorityBreakdown.low;
const highPct = totalWithPriority > 0 ? Math.round((priorityBreakdown.high / totalWithPriority) * 100) : 0;
const mediumPct = totalWithPriority > 0 ? Math.round((priorityBreakdown.medium / totalWithPriority) * 100) : 0;
const lowPct = totalWithPriority > 0 ? Math.round((priorityBreakdown.low / totalWithPriority) * 100) : 0;

// Intent percentages
const intentEntries = Object.entries(intentGroups).sort((a, b) => b[1] - a[1]);
const maxIntent = intentEntries.length > 0 ? intentEntries[0][1] : 1;

// Format percentages
const formatEntries = Object.entries(formatGroups).filter(([k]) => k !== 'unassigned').sort((a, b) => b[1] - a[1]);
const maxFormat = formatEntries.length > 0 ? formatEntries[0][1] : 1;

// Top keywords for table
const topKwList = keywords.slice(0, 25);

// High priority content
const highPrioList = highPriorityContent.slice(0, 10);
const medPrioList = mediumPriorityContent.slice(0, 5);

// Pillar-cluster structure
const pillarEntries = Object.entries(pillarClusterMap);

// Competitor stats
const topCompetitors = competitors.slice(0, 10);
const avgWordCount = stats.avg_competitor_word_count;
const maxWordCount = competitorsWithWordCount.length > 0 ? Math.max(...competitorsWithWordCount.map(c => c.word_count)) : 0;
const minWordCount = competitorsWithWordCount.length > 0 ? Math.min(...competitorsWithWordCount.map(c => c.word_count)) : 0;

const htmlReport = `<!DOCTYPE html>
<html lang="tr">
<head>
  <meta charset="UTF-8">
  <title>${project.project_name} - SEO Raporu</title>
  <style>
    *{margin:0;padding:0;box-sizing:border-box}
    body{font-family:system-ui,sans-serif;font-size:14px;color:#333;padding:40px;max-width:1100px;margin:0 auto;background:#fff}
    h1{font-size:24px;color:#1a1a2e;margin-bottom:8px}
    h2{font-size:16px;color:#16213e;margin:32px 0 16px;padding-bottom:8px;border-bottom:2px solid #e94560}
    .meta{color:#666;font-size:13px;margin-bottom:32px}
    .grid{display:grid;grid-template-columns:repeat(4,1fr);gap:16px;margin-bottom:24px}
    .box{background:#f8f9fa;border-radius:8px;padding:20px;text-align:center}
    .box .num{font-size:28px;font-weight:700;color:#e94560}
    .box .lbl{font-size:11px;color:#666;text-transform:uppercase;margin-top:4px}
    table{width:100%;border-collapse:collapse;margin-bottom:24px}
    th{background:#1a1a2e;color:#fff;padding:12px;text-align:left;font-size:12px;text-transform:uppercase}
    td{padding:10px 12px;border-bottom:1px solid #eee}
    tr:hover{background:#f8f9fa}
    .tag{display:inline-block;padding:3px 8px;border-radius:4px;font-size:11px;font-weight:600}
    .tag-high{background:#d4edda;color:#155724}
    .tag-med{background:#fff3cd;color:#856404}
    .tag-low{background:#f8d7da;color:#721c24}
    .tag-info{background:#e7f1ff;color:#004085}
    .two-col{display:grid;grid-template-columns:1fr 1fr;gap:24px}
    .bar{display:flex;align-items:center;margin-bottom:8px}
    .bar-label{width:100px;font-size:12px;color:#666}
    .bar-track{flex:1;height:20px;background:#eee;border-radius:4px;overflow:hidden}
    .bar-fill{height:100%;background:linear-gradient(90deg,#e94560,#1a1a2e);border-radius:4px}
    .bar-val{width:40px;text-align:right;font-size:12px;font-weight:600}
    .footer{margin-top:40px;padding-top:20px;border-top:1px solid #eee;text-align:center;color:#999;font-size:12px}
    @media print{body{padding:20px}h2{break-before:auto}}
  </style>
</head>
<body>
  <h1>${project.project_name}</h1>
  <div class="meta">Ana Keyword: <strong>${project.main_keyword}</strong> | Musteri: ${project.client_name || '-'} | ${new Date().toLocaleDateString('tr-TR')}</div>

  <div class="grid">
    <div class="box"><div class="num">${stats.total_keywords}</div><div class="lbl">Keyword</div></div>
    <div class="box"><div class="num">${contentPlan.pillar_pages}</div><div class="lbl">Pillar</div></div>
    <div class="box"><div class="num">${contentPlan.cluster_pages}</div><div class="lbl">Cluster</div></div>
    <div class="box"><div class="num">${Math.round(totalWordTarget/1000)}K</div><div class="lbl">Kelime Hedef</div></div>
  </div>

  <div class="two-col">
    <div>
      <h2>Oncelik Dagilimi</h2>
      <div class="bar"><div class="bar-label">Yuksek</div><div class="bar-track"><div class="bar-fill" style="width:${highPct}%"></div></div><div class="bar-val">${priorityBreakdown.high}</div></div>
      <div class="bar"><div class="bar-label">Orta</div><div class="bar-track"><div class="bar-fill" style="width:${mediumPct}%;background:#f59e0b"></div></div><div class="bar-val">${priorityBreakdown.medium}</div></div>
      <div class="bar"><div class="bar-label">Dusuk</div><div class="bar-track"><div class="bar-fill" style="width:${lowPct}%;background:#94a3b8"></div></div><div class="bar-val">${priorityBreakdown.low}</div></div>
    </div>
    <div>
      <h2>Intent Dagilimi</h2>
      ${intentEntries.slice(0,4).map(([i,c])=>`<div class="bar"><div class="bar-label">${i}</div><div class="bar-track"><div class="bar-fill" style="width:${Math.round(c/maxIntent*100)}%"></div></div><div class="bar-val">${c}</div></div>`).join('')}
    </div>
  </div>

  <h2>Oncelikli Icerikler</h2>
  <table>
    <tr><th>#</th><th>Keyword</th><th>Format</th><th>Kelime</th><th>Firsat</th><th>Oncelik</th></tr>
    ${highPrioList.concat(medPrioList).slice(0,12).map((k,i)=>`<tr><td>${i+1}</td><td><strong>${k.keyword}</strong></td><td>${k.content_format||'-'}</td><td>${k.recommended_word_count||'-'}</td><td>${k.opportunity_score||0}</td><td><span class="tag ${k.content_priority==='high'?'tag-high':'tag-med'}">${k.content_priority||'-'}</span></td></tr>`).join('')}
  </table>

  ${pillarEntries.length>0?`<h2>Pillar-Cluster Yapisi</h2>
  <table>
    <tr><th>Pillar</th><th>Cluster Sayisi</th><th>Cluster Keywordler</th></tr>
    ${pillarEntries.slice(0,5).map(([kw,d])=>`<tr><td><strong>${kw}</strong></td><td>${d.clusters.length}</td><td>${d.clusters.slice(0,4).map(c=>c.keyword).join(', ')}${d.clusters.length>4?' ...':''}</td></tr>`).join('')}
  </table>`:''}

  ${topCompetitors.length>0?`<h2>Rakip Analizi</h2>
  <div class="grid" style="grid-template-columns:repeat(3,1fr);margin-bottom:16px">
    <div class="box"><div class="num">${avgWordCount}</div><div class="lbl">Ort. Kelime</div></div>
    <div class="box"><div class="num">${maxWordCount}</div><div class="lbl">Max</div></div>
    <div class="box"><div class="num">${minWordCount}</div><div class="lbl">Min</div></div>
  </div>
  <table>
    <tr><th>Sira</th><th>Domain</th><th>Kelime</th><th>DR</th></tr>
    ${topCompetitors.slice(0,5).map(c=>`<tr><td><span class="tag tag-info">#${c.serp_position||'-'}</span></td><td>${c.competitor_domain||'-'}</td><td>${c.word_count||0}</td><td>${c.domain_rating||0}</td></tr>`).join('')}
  </table>`:''}

  <h2>Keyword Listesi</h2>
  <table>
    <tr><th>Keyword</th><th>Intent</th><th>Firsat</th><th>Oncelik</th><th>Format</th></tr>
    ${topKwList.slice(0,20).map(k=>`<tr><td>${k.keyword}</td><td>${k.search_intent||'-'}</td><td><strong>${k.opportunity_score||0}</strong></td><td><span class="tag ${k.content_priority==='high'?'tag-high':k.content_priority==='medium'?'tag-med':'tag-low'}">${k.content_priority||'-'}</span></td><td>${k.content_format||'-'}</td></tr>`).join('')}
  </table>

  <div class="footer">SEOART Content Studio | ${new Date().toLocaleString('tr-TR')}</div>
</body>
</html>`;

// ============ RETURN BASED ON FORMAT ============
return [{
  json: {
    format: format,
    project: {
      id: project.id,
      name: project.project_name,
      main_keyword: project.main_keyword,
      client_name: project.client_name,
      client_domain: project.client_domain,
      status: project.status,
      target_country: project.target_country,
      target_language: project.target_language
    },
    statistics: stats,
    priority_breakdown: priorityBreakdown,
    intent_distribution: intentGroups,
    content_format_distribution: formatGroups,
    clusters: clusters,
    content_plan: contentPlan,
    serp_features: serpFeatures,
    csv_exports: {
      summary: summaryCsv,
      keywords: keywordsCsv,
      competitors: competitorsCsv,
      paa: paaCsv
    },
    html_report: htmlReport,
    raw_data: {
      keywords: keywords,
      competitors: competitors,
      paa_questions: paaData
    },
    generated_at: new Date().toISOString()
  }
}];
