// Input validation
const projectId = $json.params?.projectId;

if (!projectId) {
  return {
    json: {
      isValid: false,
      error: 'project_id parametresi zorunludur',
      errorCode: 400
    }
  };
}

const parsedId = parseInt(projectId);
if (isNaN(parsedId) || parsedId <= 0) {
  return {
    json: {
      isValid: false,
      error: 'project_id gecerli bir pozitif sayi olmalidir',
      errorCode: 400
    }
  };
}

return {
  json: {
    isValid: true,
    projectId: parsedId
  }
};

// Build comprehensive validation response
const result = $input.first().json;
const projectId = $('Code - Validate Input').first().json.projectId;

// Check if project exists
if (!result.project_id) {
  return [{
    json: {
      success: false,
      error: 'Project not found',
      projectId: projectId,
      projectExists: false
    }
  }];
}

// Extract stats
const stats = {
  keywords: result.keyword_count || 0,
  filteredKeywords: result.filtered_keyword_count || 0,
  scoredKeywords: result.scored_keyword_count || 0,
  strategyKeywords: result.strategy_keyword_count || 0,
  competitors: result.competitor_count || 0,
  scrapedCompetitors: result.scraped_competitor_count || 0,
  serpFeatures: result.serp_feature_count || 0,
  paaQuestions: result.paa_count || 0
};

// Build tool validation status
const tools = {
  // WF-101a: Keyword Discovery - Always can run if project exists
  'discovery': {
    id: 'WF-101a',
    name: 'Keyword Discovery',
    canRun: true,
    hasRun: stats.keywords > 0,
    dataCount: stats.keywords,
    prerequisites: [],
    missingPrerequisites: []
  },

  // WF-101b: Filter - Needs keywords
  'filter': {
    id: 'WF-101b',
    name: 'AI Filtreleme',
    canRun: stats.keywords > 0,
    hasRun: stats.filteredKeywords > 0,
    dataCount: stats.filteredKeywords,
    prerequisites: ['discovery'],
    missingPrerequisites: stats.keywords === 0 ? ['discovery'] : []
  },

  // WF-104: Competitor Analyzer - Always can run if project exists
  'competitors': {
    id: 'WF-104',
    name: 'Rakip Analizi',
    canRun: true,
    hasRun: stats.competitors > 0,
    dataCount: stats.competitors,
    prerequisites: [],
    missingPrerequisites: []
  },

  // WF-104b: Competitor Scraper - Needs competitor URLs
  'scraper': {
    id: 'WF-104b',
    name: 'Rakip Scraper',
    canRun: stats.competitors > 0,
    hasRun: stats.scrapedCompetitors > 0,
    dataCount: stats.scrapedCompetitors,
    prerequisites: ['competitors'],
    missingPrerequisites: stats.competitors === 0 ? ['competitors'] : []
  },

  // WF-105: SERP Detector - Always can run if project exists
  'serp': {
    id: 'WF-105',
    name: 'SERP Analizi',
    canRun: true,
    hasRun: stats.serpFeatures > 0,
    dataCount: stats.serpFeatures,
    prerequisites: [],
    missingPrerequisites: []
  },

  // WF-106: Content Gap - Needs keywords OR competitors
  'contentGap': {
    id: 'WF-106',
    name: 'Icerik Boslugu',
    canRun: stats.keywords > 0 || stats.scrapedCompetitors > 0,
    hasRun: false, // Content gap adds keywords, hard to track separately
    dataCount: 0,
    prerequisites: ['discovery', 'scraper'],
    missingPrerequisites: [
      ...(stats.keywords === 0 ? ['discovery'] : []),
      ...(stats.scrapedCompetitors === 0 ? ['scraper'] : [])
    ],
    note: 'En az keyword veya scraped competitor gerekli'
  },

  // WF-107: Opportunity Scorer - Needs keywords
  'opportunity': {
    id: 'WF-107',
    name: 'Firsat Skorlari',
    canRun: stats.keywords > 0,
    hasRun: stats.scoredKeywords > 0,
    dataCount: stats.scoredKeywords,
    prerequisites: ['discovery'],
    missingPrerequisites: stats.keywords === 0 ? ['discovery'] : [],
    recommended: stats.filteredKeywords === 0 ? ['filter'] : []
  },

  // WF-109: Strategy Generator - Needs keywords (preferably with scores)
  'strategy': {
    id: 'WF-109',
    name: 'Icerik Stratejisi',
    canRun: stats.keywords > 0,
    hasRun: stats.strategyKeywords > 0,
    dataCount: stats.strategyKeywords,
    prerequisites: ['discovery'],
    missingPrerequisites: stats.keywords === 0 ? ['discovery'] : [],
    recommended: [
      ...(stats.scoredKeywords === 0 ? ['opportunity'] : []),
      ...(stats.scrapedCompetitors === 0 ? ['scraper'] : [])
    ]
  }
};

// Calculate overall progress
const completedSteps = Object.values(tools).filter(t => t.hasRun).length;
const totalSteps = Object.keys(tools).length;
const progress = Math.round((completedSteps / totalSteps) * 100);

// Determine next recommended step
let nextStep = null;
const stepOrder = ['discovery', 'filter', 'competitors', 'scraper', 'serp', 'contentGap', 'opportunity', 'strategy'];
for (const step of stepOrder) {
  if (tools[step].canRun && !tools[step].hasRun) {
    nextStep = step;
    break;
  }
}

return [{
  json: {
    success: true,
    project: {
      id: result.project_id,
      uuid: result.project_uuid,
      name: result.project_name,
      mainKeyword: result.main_keyword,
      status: result.project_status,
      createdAt: result.project_created_at
    },
    stats: stats,
    tools: tools,
    progress: {
      completed: completedSteps,
      total: totalSteps,
      percentage: progress
    },
    nextStep: nextStep,
    nextStepInfo: nextStep ? tools[nextStep] : null
  }
}];
