// Input validation
const id = $json.params?.id;

if (!id) {
  return {
    json: {
      isValid: false,
      error: 'id parametresi zorunludur',
      errorCode: 400
    }
  };
}

// Check if it's a numeric ID or UUID
const isNumeric = /^\d+$/.test(id);
const isUUID = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i.test(id);

if (!isNumeric && !isUUID) {
  return {
    json: {
      isValid: false,
      error: 'id gecerli bir sayi veya UUID olmalidir',
      errorCode: 400
    }
  };
}

return {
  json: {
    isValid: true,
    id: id,
    isNumeric: isNumeric,
    isUUID: isUUID
  }
};

// Check if project exists
const result = $input.first().json;
const validatedData = $('Code - Validate Input').first().json;

if (!result.project_id) {
  return [{
    json: {
      projectExists: false,
      id: validatedData.id
    }
  }];
}

return [{
  json: {
    projectExists: true,
    projectId: result.project_id
  }
}];

// Format response
const project = $input.first().json;

return [{
  json: {
    success: true,
    data: {
      id: project.id,
      uuid: project.uuid,
      project_name: project.project_name,
      main_keyword: project.main_keyword,
      target_country: project.target_country,
      target_language: project.target_language,
      status: project.status,
      total_keywords_found: project.total_keywords_found || 0,
      total_competitors_analyzed: project.total_competitors_analyzed || 0,
      total_paa_found: project.total_paa_found || 0,
      created_at: project.created_at,
      updated_at: project.updated_at,
      client: project.client_id ? {
        id: project.client_id,
        name: project.client_name
      } : null,
      stats: {
        keyword_count: parseInt(project.keyword_count) || 0,
        analyzed_count: parseInt(project.analyzed_count) || 0
      }
    }
  }
}];
