// Input validation
const projectId = $json.params?.projectId;

if (!projectId) {
  return {
    json: {
      isValid: false,
      error: 'projectId parametresi zorunludur',
      errorCode: 400
    }
  };
}

const parsedId = parseInt(projectId);
if (isNaN(parsedId) || parsedId <= 0) {
  return {
    json: {
      isValid: false,
      error: 'projectId gecerli bir pozitif sayi olmalidir',
      errorCode: 400
    }
  };
}

return {
  json: {
    isValid: true,
    projectId: parsedId
  }
};

// Check if project exists
const result = $input.first().json;
const validatedData = $('Code - Validate Input').first().json;

if (!result.project_id) {
  return [{
    json: {
      projectExists: false,
      projectId: validatedData.projectId
    }
  }];
}

return [{
  json: {
    projectExists: true,
    projectId: result.project_id,
    projectName: result.project_name
  }
}];

// Check if there are unscraped competitors
const items = $input.all();
const projectData = $('IF - Project Exists').first().json;

const hasCompetitors = items.length > 0 && !!items[0].json.id;

if (!hasCompetitors) {
  return [{
    json: {
      hasCompetitors: false,
      projectId: projectData.projectId,
      message: 'Scrape edilecek rakip bulunamadi',
      competitors: []
    }
  }];
}

// Return competitors list in single item for sequential processing
const competitors = items.map(item => ({
  competitorId: item.json.id,
  url: item.json.url,
  domain: item.json.domain,
  projectId: item.json.project_id
}));

return [{
  json: {
    hasCompetitors: true,
    projectId: projectData.projectId,
    competitors: competitors
  }
}];

const inputData = $input.first().json;
const competitors = inputData.competitors || [];

const sanitize = (str) => {
  if (!str) return '';
  return str.replace(/[\x00-\x1F\x7F]/g, '').replace(/\\/g, '').replace(/[\"']/g, '').trim();
};

const escapeForMysql = (str) => {
  if (!str) return '';
  return str.replace(/'/g, "''");
};

const parseHtml = (html, comp) => {
  const fail = { competitorId: comp.competitorId, projectId: comp.projectId, url: comp.url, wordCount: 0, h1Count: 0, h2Count: 0, h3Count: 0, h4Count: 0, h5Count: 0, h6Count: 0, headingsJson: '{"h1":[],"h2":[],"h3":[],"h4":[],"h5":[],"h6":[]}', scrapeStatus: 'failed' };
  if (!html || html.length < 100) return fail;

  let text = html.replace(/<script[\s\S]*?<\/script>/gi, '').replace(/<style[\s\S]*?<\/style>/gi, '').replace(/<[^>]+>/g, ' ').replace(/\s+/g, ' ').trim();
  const wc = text.split(' ').filter(w => w.length > 0).length;

  const getH = (n) => {
    const r = new RegExp('<h' + n + '[^>]*>([\\s\\S]*?)</h' + n + '>', 'gi');
    const m = html.match(r) || [];
    return m.map(x => sanitize(x.replace(/<[^>]+>/g, '').substring(0, 150)));
  };

  const h1=getH(1), h2=getH(2), h3=getH(3), h4=getH(4), h5=getH(5), h6=getH(6);
  const toArr = (a) => '[' + a.slice(0,20).map(s => '"' + s + '"').join(',') + ']';
  const js = '{"h1":'+toArr(h1)+',"h2":'+toArr(h2)+',"h3":'+toArr(h3)+',"h4":'+toArr(h4)+',"h5":'+toArr(h5)+',"h6":'+toArr(h6)+'}';

  return { competitorId: comp.competitorId, projectId: comp.projectId, url: comp.url, wordCount: wc, h1Count: h1.length, h2Count: h2.length, h3Count: h3.length, h4Count: h4.length, h5Count: h5.length, h6Count: h6.length, headingsJson: escapeForMysql(js), scrapeStatus: 'success' };
};

const results = [];
for (const c of competitors) {
  try {
    const res = await this.helpers.httpRequest({ method: 'GET', url: c.url, timeout: 15000, returnFullResponse: true, ignoreHttpStatusErrors: true });
    results.push({ json: parseHtml(res.body || '', c) });
  } catch (e) {
    results.push({ json: parseHtml('', c) });
  }
}
return results;

// Prepare success response
const allItems = $input.all();
const successCount = allItems.filter(i => i.json.affectedRows > 0).length;
const projectId = $('IF - Project Exists').first().json.projectId;

return [{
  json: {
    success: true,
    data: {
      projectId: projectId,
      scraped_count: successCount,
      message: `${successCount} rakip sayfasi basariyla scrape edildi`
    }
  }
}];
