// Input validation
const projectId = $json.params?.projectId;

if (!projectId) {
  return {
    json: {
      isValid: false,
      error: 'projectId parametresi zorunludur',
      errorCode: 400
    }
  };
}

const parsedId = parseInt(projectId);
if (isNaN(parsedId) || parsedId <= 0) {
  return {
    json: {
      isValid: false,
      error: 'projectId gecerli bir pozitif sayi olmalidir',
      errorCode: 400
    }
  };
}

return {
  json: {
    isValid: true,
    projectId: parsedId
  }
};

// Check if project exists
const result = $input.first().json;
const validatedData = $('Code - Validate Input').first().json;

if (!result.project_id) {
  return [{
    json: {
      projectExists: false,
      projectId: validatedData.projectId
    }
  }];
}

return [{
  json: {
    projectExists: true,
    projectId: result.project_id,
    projectName: result.project_name,
    mainKeyword: result.main_keyword
  }
}];

// Format response
const competitorItems = $input.all();
const projectData = $('IF - Project Exists').first().json;

// Check if we have competitors
const hasCompetitors = competitorItems.length > 0 && !!competitorItems[0].json.id;
const competitors = hasCompetitors ? competitorItems.map(item => {
  const c = item.json;
  let headings = null;
  try {
    headings = c.headings_json ? JSON.parse(c.headings_json) : null;
  } catch (e) {
    headings = null;
  }

  return {
    id: c.id,
    url: c.competitor_url,
    domain: c.competitor_domain,
    position: c.serp_position,
    title: c.title,
    description: c.meta_description,
    content_stats: {
      word_count: c.word_count || 0,
      h1_count: c.h1_count || 0,
      h2_count: c.h2_count || 0,
      h3_count: c.h3_count || 0,
      h4_count: c.h4_count || 0,
      h5_count: c.h5_count || 0,
      h6_count: c.h6_count || 0
    },
    headings: headings,
    seo_metrics: {
      domain_rating: c.domain_rating,
      backlinks: c.backlinks_count
    },
    source: c.source,
    analyzed_at: c.analyzed_at
  };
}) : [];

// Calculate averages
const avgWordCount = competitors.length > 0 
  ? Math.round(competitors.reduce((sum, c) => sum + (c.content_stats.word_count || 0), 0) / competitors.length)
  : 0;
const avgH2Count = competitors.length > 0
  ? Math.round(competitors.reduce((sum, c) => sum + (c.content_stats.h2_count || 0), 0) / competitors.length)
  : 0;

return [{
  json: {
    success: true,
    project: {
      id: projectData.projectId,
      name: projectData.projectName,
      main_keyword: projectData.mainKeyword
    },
    data: competitors,
    stats: {
      total_competitors: competitors.length,
      avg_word_count: avgWordCount,
      avg_h2_count: avgH2Count,
      scraped_count: competitors.filter(c => c.content_stats.word_count > 0).length
    }
  }
}];
