const body = $json.body || $json;
const keywords = body.keywords || [];
const projectId = body.project_id;
const clientId = body.client_id;
const country = (body.country || 'TR').toUpperCase();
const language = body.language || 'tr';

if (!Array.isArray(keywords) || keywords.length === 0) {
  return [{json: {error: 'keywords array required', code: 400}}];
}

const seeds = keywords
  .map(k => String(k).trim().toLowerCase())
  .filter(k => k.length >= 2)
  .slice(0, 20);

if (seeds.length === 0) {
  return [{json: {error: 'valid keywords required', code: 400}}];
}

const locCodes = {TR: 2792, US: 2840, GB: 2826, DE: 2276, FR: 2250};
const locationCode = locCodes[country] || 2792;

return seeds.map((seed, idx) => ({
  json: {
    seed,
    seedIndex: idx,
    totalSeeds: seeds.length,
    locationCode,
    language,
    projectId: projectId ? parseInt(projectId) : null,
    clientId: clientId ? parseInt(clientId) : null,
    startTime: Date.now()
  }
}));

const items = $input.all();
const firstItem = $('Validate').first().json;
const projectId = firstItem.projectId;
const clientId = firstItem.clientId;
const startTime = firstItem.startTime;
const totalSeeds = firstItem.totalSeeds;

const allApproved = [];
const allRejected = [];
const perSeed = {};

items.forEach(item => {
  const r = item.json;
  if (r.seed) {
    allApproved.push(...(r.approved || []));
    allRejected.push(...(r.rejected || []));
    perSeed[r.seed] = r.stats || {approved: 0, rejected: 0};
  }
});

return [{json: {
  projectId,
  clientId,
  stats: {
    total_seeds: totalSeeds,
    total_approved: allApproved.length,
    total_rejected: allRejected.length,
    per_seed: perSeed
  },
  approved_keywords: allApproved,
  rejected_keywords: allRejected,
  startTime
}}];

const d = $input.first().json;
const all = [...(d.approved_keywords || []), ...(d.rejected_keywords || [])];

if (!all.length || !d.projectId) {
  return [{json: {...d, query: 'SELECT 1'}}];
}

const esc = s => s == null ? 'NULL' : "'" + String(s).replace(/'/g, "''") + "'";

const vals = all.slice(0, 2000).map(k => 
  `(${d.projectId},${esc(k.keyword)},${esc(k.seed_keyword)},'related',${k.search_volume || 'NULL'},${k.cpc || 'NULL'},NULL,NULL,${esc(k.seed_keyword)},${esc(k.priority || 'medium')},'cluster','dataforseo','${k.status}',NULL)`
).join(',');

const query = `SET NAMES utf8mb4; INSERT INTO keyword_results(project_id,keyword,seed_keyword,keyword_type,search_volume,cpc,competition,search_intent,keyword_cluster,content_priority,page_type,source,status,reject_reason) VALUES ${vals} ON DUPLICATE KEY UPDATE search_volume=VALUES(search_volume),status=VALUES(status),seed_keyword=VALUES(seed_keyword)`;

return [{json: {...d, query}}];

const d = $('Build SQL').first().json;
return [{json: {
  success: true,
  message: `Bulk tamamlandi: ${d.approved_keywords?.length || 0} ana sonuc, ${d.rejected_keywords?.length || 0} diger`,
  project_id: d.projectId,
  stats: d.stats,
  keywords: (d.approved_keywords || []).map(k => ({
    keyword: k.keyword,
    seed_keyword: k.seed_keyword,
    search_volume: k.search_volume,
    priority: k.priority,
    status: k.status
  })),
  rejected_keywords: (d.rejected_keywords || []).map(k => ({
    keyword: k.keyword,
    seed_keyword: k.seed_keyword,
    search_volume: k.search_volume
  })),
  processing_time_ms: Date.now() - d.startTime
}}];

