const body = $json.body || {};
const spreadsheetId = body.spreadsheet_id;
const sheetName = body.sheet_name;
const row = body.row || 1;
const columnMappings = body.column_mappings || {};

if (!spreadsheetId || !sheetName) {
  return { json: { success: false, error: 'spreadsheet_id ve sheet_name gerekli' } };
}

// Get column letters sorted
const columns = Object.entries(columnMappings).sort((a, b) => a[1].localeCompare(b[1]));
const firstCol = columns[0]?.[1] || 'A';
const lastCol = columns[columns.length - 1]?.[1] || 'A';
const rowRange = `${sheetName}!${firstCol}${row}:${lastCol}${row}`;

return { json: {
  spreadsheet_id: spreadsheetId,
  sheet_name: sheetName,
  row,
  row_range: rowRange,
  column_mappings: columnMappings,
  columns: columns
}};

const prepData = $('Prepare').item.json;
const response = $json;
const rowValues = response.values?.[0] || [];
const columns = prepData.columns || [];

// Map values back to field names
const values = {};
columns.forEach(([field, col], index) => {
  values[field] = rowValues[index] || '';
});

return { json: {
  success: true,
  row: prepData.row,
  values: values
}};
