const body = $json.body || {};
const spreadsheetId = body.spreadsheet_id;
const sheetName = body.sheet_name;

if (!spreadsheetId || !sheetName) {
  return { json: { success: false, error: 'spreadsheet_id ve sheet_name gerekli' } };
}

return { json: {
  spreadsheet_id: spreadsheetId,
  sheet_name: sheetName
}};

const prepData = $('Prepare').item.json;
const response = $json;

try {
  const spreadsheetName = response.properties?.title || '';
  const sheets = response.sheets || [];

  // Find the requested sheet
  const targetSheet = sheets.find(s => s.properties?.title === prepData.sheet_name);

  if (!targetSheet) {
    return { json: {
      success: false,
      error: `Sheet '${prepData.sheet_name}' bulunamadı`
    }};
  }

  const rowCount = targetSheet.properties?.gridProperties?.rowCount || 0;

  return { json: {
    success: true,
    spreadsheet_name: spreadsheetName,
    sheet_name: prepData.sheet_name,
    row_count: rowCount
  }};
} catch (err) {
  return { json: {
    success: false,
    error: 'Sheet bilgileri alınamadı: ' + err.message
  }};
}
