// ===========================================
// INPUT VALIDATION
// ===========================================
const body = $json.body || $json;
const keyword = body.keyword || body.main_keyword;
const projectId = body.project_id;
const clientId = body.client_id;
const country = (body.country || 'TR').toUpperCase();
const language = body.language || 'tr';
const customRules = body.custom_rules || '';

if (!keyword || keyword.trim().length < 2) {
  return [{ json: { 
    isValid: false, 
    error: 'keyword parametresi zorunludur (min 2 karakter)', 
    errorCode: 400 
  }}];
}

const locationCodes = {
  'TR': 2792, 'US': 2840, 'GB': 2826, 'DE': 2276, 'FR': 2250
};

return [{ json: { 
  isValid: true, 
  keyword: keyword.trim().toLowerCase(),
  projectId: projectId ? parseInt(projectId) : null,
  clientId: clientId ? parseInt(clientId) : null,
  country: country,
  language: language,
  locationCode: locationCodes[country] || 2792,
  customRules: customRules
}}];

// ===========================================
// PREPARE CONFIG FOR API CALLS
// ===========================================
const input = $('Validate Input').first().json;
const config = $input.first().json;

return [{ json: {
  ...input,
  enableAiAnalysis: config.enable_ai_analysis == 1 || config.enable_ai_analysis === null,
  aiModel: config.ai_model_preference || 'gemini-2.0-flash',
  aiTemperature: parseFloat(config.ai_temperature) || 0.3,
  filterPromptTemplate: config.filter_prompt || ''
}}];

// ===========================================
// MERGE ALL KEYWORD SOURCES + TURKISH NORMALIZATION
// ===========================================
const config = $('Prepare Config').first().json;
const mainKeyword = config.keyword;

function normalizeForComparison(str) {
  return str.toLowerCase()
    .replace(/Ä±/g, 'i').replace(/Ä°/g, 'i')
    .replace(/ÅŸ/g, 's').replace(/Åž/g, 's')
    .replace(/Ã¶/g, 'o').replace(/Ã–/g, 'o')
    .replace(/Ã¼/g, 'u').replace(/Ãœ/g, 'u')
    .replace(/Ã§/g, 'c').replace(/Ã‡/g, 'c')
    .replace(/ÄŸ/g, 'g').replace(/Äž/g, 'g')
    .replace(/[^a-z0-9\s]/g, '')
    .replace(/\s+/g, ' ')
    .trim();
}

function hasTurkishChars(str) {
  return /[Ä±Ä°ÅŸÅžÃ¶Ã–Ã¼ÃœÃ§Ã‡ÄŸÄž]/.test(str);
}

let suggestions = [];
try {
  const sugResponse = $('Google Suggestions').first().json;
  if (Array.isArray(sugResponse) && Array.isArray(sugResponse[1])) {
    suggestions = sugResponse[1].map(s => ({
      keyword: String(s).toLowerCase(),
      search_volume: null,
      cpc: null,
      competition: null,
      source: 'google_suggest'
    }));
  }
} catch (e) { console.log('Suggestions parse error:', e.message); }

let dataforseoKeywords = [];
try {
  const dfsResponse = $('DataForSEO Keywords').first().json;
  if (dfsResponse?.tasks?.[0]?.result && Array.isArray(dfsResponse.tasks[0].result)) {
    dataforseoKeywords = dfsResponse.tasks[0].result.map(kw => ({
      keyword: (kw.keyword || '').toLowerCase(),
      search_volume: kw.search_volume || 0,
      cpc: kw.cpc || 0,
      competition: kw.competition || null,
      competition_index: kw.competition_index || 0,
      source: 'dataforseo'
    })).filter(kw => kw.keyword);
  }
} catch (e) { console.log('DataForSEO parse error:', e.message); }

const allKeywords = [...dataforseoKeywords, ...suggestions];
const seen = new Map();

allKeywords.forEach(kw => {
  if (!kw.keyword || kw.keyword.length < 2) return;
  const normalizedKey = normalizeForComparison(kw.keyword);
  if (!normalizedKey || normalizedKey.length < 2) return;

  if (!seen.has(normalizedKey)) {
    seen.set(normalizedKey, kw);
  } else {
    const existing = seen.get(normalizedKey);
    if (hasTurkishChars(kw.keyword) && !hasTurkishChars(existing.keyword)) {
      kw.search_volume = kw.search_volume || existing.search_volume;
      kw.cpc = kw.cpc || existing.cpc;
      kw.source = existing.source + ',' + kw.source;
      seen.set(normalizedKey, kw);
    } else if (kw.search_volume && !existing.search_volume) {
      existing.search_volume = kw.search_volume;
      existing.cpc = kw.cpc;
    }
  }
});

const uniqueKeywords = Array.from(seen.values());

const mainNormalized = normalizeForComparison(mainKeyword);
if (!seen.has(mainNormalized)) {
  uniqueKeywords.unshift({
    keyword: mainKeyword,
    search_volume: null,
    cpc: null,
    competition: null,
    source: 'seed'
  });
}

return [{
  json: {
    mainKeyword: mainKeyword,
    projectId: config.projectId,
    clientId: config.clientId,
    country: config.country,
    language: config.language,
    customRules: config.customRules,
    enableAiAnalysis: config.enableAiAnalysis,
    aiModel: config.aiModel,
    aiTemperature: config.aiTemperature,
    filterPromptTemplate: config.filterPromptTemplate,
    keywords: uniqueKeywords,
    stats: {
      total_raw: allKeywords.length,
      after_dedup: uniqueKeywords.length,
      from_google_suggest: suggestions.length,
      from_dataforseo: dataforseoKeywords.length
    }
  }
}];

// ===========================================
// PREPARE FILTER PROMPT FROM DATABASE
// Uses keyword-filter system prompt
// ===========================================
const data = $input.first().json;
const keywords = data.keywords || [];
const mainKeyword = data.mainKeyword;
const country = data.country;
const language = data.language;
const customRules = data.customRules || '';
let promptTemplate = data.filterPromptTemplate || '';

if (keywords.length === 0) {
  return [{ json: { skip: true, message: 'No keywords to filter' } }];
}

if (!data.enableAiAnalysis) {
  return [{ json: { skipAi: true, ...data } }];
}

// Sort by volume and limit to top 150 for AI processing
const sortedKeywords = [...keywords]
  .sort((a, b) => (b.search_volume || 0) - (a.search_volume || 0))
  .slice(0, 150);

// Build keyword CSV
const keywordCsv = sortedKeywords.map((kw, i) => {
  const vol = kw.search_volume || 0;
  const cpc = kw.cpc ? kw.cpc.toFixed(2) : '0';
  const comp = kw.competition || '';
  const src = kw.source || '';
  return `${i+1}|${kw.keyword}|${vol}|${cpc}|${comp}|${src}`;
}).join('\n');

// Use DB prompt or fallback
let prompt = '';
if (promptTemplate) {
  prompt = promptTemplate
    .replace(/\{\{main_keyword\}\}/g, mainKeyword)
    .replace(/\{\{country\}\}/g, country)
    .replace(/\{\{language\}\}/g, language)
    .replace(/\{\{keyword_list\}\}/g, keywordCsv);
} else {
  // Fallback prompt if DB prompt not available
  prompt = `Sen bir SEO uzmanisin. Keyword listesini analiz et, filtrele ve zenginlestir.

Ana Keyword: ${mainKeyword}
Ulke: ${country}

KEYWORDS (No|Keyword|Volume|CPC|Competition|Source):
${keywordCsv}

GOREV:
1. Alakasiz, spam, tarih iceren keywordleri REJECT et
2. Her keyword icin intent belirle (informational, commercial, transactional, navigational)
3. Semantik kumeleme yap (3-8 kume)
4. Oncelik belirle (high, medium, low)
5. Icerik tipi oner (pillar, cluster, landing, comparison, faq, guide)

CIKTI FORMATI (sadece CSV):
No|status|intent|cluster|priority|content_type

status: approved veya rejected
rejected icin diger alanlar bos

Ornek:
1|approved|commercial|Fiyat Arastirmasi|high|comparison
2|rejected|||||
3|approved|informational|Baslangic Rehberi|medium|cluster`;
}

// Add custom rules if provided
if (customRules) {
  prompt += `\n\n---\n\nðŸŽ¯ Ã–ZEL KURALLAR (Bu kurallara kesinlikle uy):\n${customRules}`;
}

return [{
  json: {
    ...data,
    skip: false,
    skipAi: false,
    filterPrompt: prompt,
    processedKeywords: sortedKeywords
  }
}];

// ===========================================
// PARSE FILTER RESPONSE (Approved/Rejected)
// ===========================================
const prevData = $('Prepare Filter Prompt').first().json;
const geminiResponse = $input.first().json;
const processedKeywords = prevData.processedKeywords || [];

const approved = [];
const rejected = [];

try {
  const text = geminiResponse?.candidates?.[0]?.content?.parts?.[0]?.text || '';
  const lines = text.split('\n').filter(l => /^\d+\|/.test(l.trim()));

  for (const line of lines) {
    const parts = line.split('|').map(p => p.trim());
    if (parts.length < 2) continue;

    const no = parseInt(parts[0]);
    const status = (parts[1] || '').toLowerCase();

    if (isNaN(no) || no < 1 || no > processedKeywords.length) continue;

    const original = processedKeywords[no - 1];

    if (status === 'approved') {
      approved.push({
        ...original,
        index: no,
        intent: parts[2] && parts[2] !== '' ? parts[2].toLowerCase() : null,
        cluster: parts[3] && parts[3] !== '' ? parts[3] : null,
        priority: parts[4] && parts[4] !== '' ? parts[4].toLowerCase() : 'medium',
        content_type: parts[5] && parts[5] !== '' ? parts[5].toLowerCase() : 'cluster'
      });
    } else if (status === 'rejected') {
      rejected.push({
        ...original,
        index: no,
        reject_reason: 'AI filtered'
      });
    }
  }

  // If AI didn't process all keywords, approve remaining with defaults
  const processedIndices = new Set([...approved, ...rejected].map(k => k.index));
  processedKeywords.forEach((kw, idx) => {
    if (!processedIndices.has(idx + 1)) {
      approved.push({
        ...kw,
        index: idx + 1,
        intent: null,
        cluster: 'Genel',
        priority: 'medium',
        content_type: 'cluster'
      });
    }
  });

} catch (e) { 
  console.log('Parse error:', e.message);
  // Fallback: approve all with defaults
  processedKeywords.forEach((kw, idx) => {
    approved.push({
      ...kw,
      index: idx + 1,
      intent: null,
      cluster: 'Genel',
      priority: 'medium',
      content_type: 'cluster'
    });
  });
}

// Generate clusters
const clusters = {};
approved.forEach(kw => {
  const c = kw.cluster || 'Genel';
  if (!clusters[c]) clusters[c] = [];
  clusters[c].push(kw.keyword);
});

return [{
  json: {
    mainKeyword: prevData.mainKeyword,
    projectId: prevData.projectId,
    clientId: prevData.clientId,
    stats: {
      ...prevData.stats,
      total_processed: processedKeywords.length,
      approved_count: approved.length,
      rejected_count: rejected.length
    },
    approved_keywords: approved,
    rejected_keywords: rejected,
    clusters: clusters,
    ai_used: true
  }
}];

// ===========================================
// SKIP AI - Use keywords as-is with defaults
// ===========================================
const data = $input.first().json;
const keywords = data.keywords || [];

const approvedKeywords = keywords.slice(0, 50).map((kw, idx) => ({
  ...kw,
  index: idx + 1,
  intent: null,
  cluster: 'Genel',
  priority: 'medium',
  content_type: 'cluster'
}));

return [{
  json: {
    mainKeyword: data.mainKeyword,
    projectId: data.projectId,
    clientId: data.clientId,
    stats: {
      ...data.stats,
      approved_count: approvedKeywords.length,
      rejected_count: 0
    },
    approved_keywords: approvedKeywords,
    rejected_keywords: [],
    clusters: { 'Genel': approvedKeywords.map(k => k.keyword) },
    ai_used: false
  }
}];

// ===========================================
// PREPARE FOR DB INSERT - WITH SQL ESCAPE
// ===========================================
let data = null;
try { data = $('Parse Filter Response').first().json; } catch(e) {}
if (!data) { try { data = $('Skip AI Filter').first().json; } catch(e) {} }
if (!data) { data = $input.first().json; }

const projectId = data.projectId;
const approvedKeywords = data.approved_keywords || [];

if (approvedKeywords.length === 0) {
  return [{ json: { skip: true } }];
}

// SQL escape function
function esc(str) {
  if (str === null || str === undefined) return null;
  return String(str).replace(/\\/g, '\\\\').replace(/'/g, "''");
}

return approvedKeywords.map((kw, idx) => ({
  json: {
    project_id: projectId,
    keyword: esc(kw.keyword),
    keyword_type: kw.source?.includes('suggest') ? 'google_suggest' : 'related',
    search_volume: kw.search_volume || null,
    cpc: kw.cpc || null,
    competition: kw.competition || null,
    search_intent: esc(kw.intent),
    keyword_cluster: esc(kw.cluster),
    content_priority: kw.priority || 'medium',
    page_type: kw.content_type || null,
    source: kw.source || 'dataforseo'
  }
}));

// ===========================================
// PREPARE FINAL RESPONSE
// ===========================================
let data = null;
try { data = $('Parse Filter Response').first().json; } catch(e) {}
if (!data) { try { data = $('Skip AI Filter').first().json; } catch(e) {} }

if (!data) {
  return [{ json: { success: false, error: 'Could not retrieve data' } }];
}

const approvedKeywords = data.approved_keywords || [];
const rejectedKeywords = data.rejected_keywords || [];
const savedCount = $input.all().length;

return [{
  json: {
    success: true,
    message: `Keyword arastirmasi tamamlandi. ${approvedKeywords.length} keyword onaylandi, ${rejectedKeywords.length} reddedildi, ${savedCount} DB'ye kaydedildi.`,
    main_keyword: data.mainKeyword,
    project_id: data.projectId,
    stats: {
      ...data.stats,
      saved_to_db: savedCount
    },
    keywords: approvedKeywords.map(kw => ({
      keyword: kw.keyword,
      search_volume: kw.search_volume,
      cpc: kw.cpc,
      competition: kw.competition,
      intent: kw.intent,
      cluster: kw.cluster,
      priority: kw.priority,
      content_type: kw.content_type,
      source: kw.source
    })),
    rejected: rejectedKeywords.map(kw => ({
      keyword: kw.keyword,
      reason: kw.reject_reason
    })),
    clusters: data.clusters || {},
    ai_used: data.ai_used
  }
}];

// ===========================================
// PREPARE RESPONSE (NO DB SAVE)
// ===========================================
let data = null;
try { data = $('Parse Filter Response').first().json; } catch(e) {}
if (!data) { try { data = $('Skip AI Filter').first().json; } catch(e) {} }
if (!data) { data = $input.first().json; }

const approvedKeywords = data.approved_keywords || [];
const rejectedKeywords = data.rejected_keywords || [];

return [{
  json: {
    success: true,
    message: `Keyword arastirmasi tamamlandi. ${approvedKeywords.length} keyword onaylandi, ${rejectedKeywords.length} reddedildi.`,
    main_keyword: data.mainKeyword,
    project_id: null,
    stats: {
      ...data.stats,
      saved_to_db: 0
    },
    keywords: approvedKeywords.map(kw => ({
      keyword: kw.keyword,
      search_volume: kw.search_volume,
      cpc: kw.cpc,
      competition: kw.competition,
      intent: kw.intent,
      cluster: kw.cluster,
      priority: kw.priority,
      content_type: kw.content_type,
      source: kw.source
    })),
    rejected: rejectedKeywords.map(kw => ({
      keyword: kw.keyword,
      reason: kw.reject_reason
    })),
    clusters: data.clusters || {},
    ai_used: data.ai_used
  }
}];
